import React, { useState } from 'react';
import './App.css';

interface OrderForm {
  customerName: string;
  email: string;
  phone: string;
  address: string;
  city: string;
  state: string;
  zipCode: string;
  furnitureType: string;
  quantity: number;
  color: string;
  deliveryDate: string;
  specialInstructions: string;
}

function App() {
  const [formData, setFormData] = useState<OrderForm>({
    customerName: '',
    email: '',
    phone: '',
    address: '',
    city: '',
    state: '',
    zipCode: '',
    furnitureType: '',
    quantity: 1,
    color: '',
    deliveryDate: '',
    specialInstructions: ''
  });

  const [phoneError, setPhoneError] = useState<string>('');

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));

    // Phone number validation
    if (name === 'phone') {
      const phoneNumber = value.replace(/\D/g, ''); // Remove non-digits
      if (phoneNumber.length !== 10) {
        setPhoneError('Phone number must be 10 digits');
      } else {
        setPhoneError('');
      }
    }
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    const phoneNumber = formData.phone.replace(/\D/g, '');
    if (phoneNumber.length !== 10) {
      setPhoneError('Phone number must be 10 digits');
      return;
    }
    console.log('Form submitted:', formData);
    // Here you would typically send the data to your backend
    alert('Order submitted successfully!');
  };

  return (
    <div className="App">
      <div className="container">
        <div className="header">
          <img src="/images/logo.png" alt="Carolina Furniture Concepts Logo" className="logo" />
          <h1>Carolina Furniture Concepts</h1>
          <p className="subtitle">Order Form</p>
        </div>

        <form onSubmit={handleSubmit} className="order-form">
          <div className="form-section">
            <h2>Customer Information</h2>
            <div className="form-group">
              <label htmlFor="customerName">Full Name *</label>
              <input
                type="text"
                id="customerName"
                name="customerName"
                value={formData.customerName}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="email">Email *</label>
              <input
                type="email"
                id="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="phone">Phone Number *</label>
              <div className="phone-input-container">
                <select className="country-code" disabled>
                  <option value="+1">+1</option>
                </select>
                <input
                  type="tel"
                  id="phone"
                  name="phone"
                  value={formData.phone}
                  onChange={handleChange}
                  placeholder="(555) 555-5555"
                  pattern="[0-9]{10}"
                  maxLength="10"
                  required
                />
              </div>
              {phoneError && <span className="error-message">{phoneError}</span>}
            </div>
          </div>

          <div className="form-section">
            <h2>Delivery Address</h2>
            <div className="form-group">
              <label htmlFor="address">Street Address *</label>
              <input
                type="text"
                id="address"
                name="address"
                value={formData.address}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-row">
              <div className="form-group">
                <label htmlFor="city">City *</label>
                <input
                  type="text"
                  id="city"
                  name="city"
                  value={formData.city}
                  onChange={handleChange}
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="state">State *</label>
                <input
                  type="text"
                  id="state"
                  name="state"
                  value={formData.state}
                  onChange={handleChange}
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="zipCode">ZIP Code *</label>
                <input
                  type="text"
                  id="zipCode"
                  name="zipCode"
                  value={formData.zipCode}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>
          </div>

          <div className="form-section">
            <h2>Order Details</h2>
            <div className="form-row">
              <div className="form-group">
                <label htmlFor="furnitureType">Furniture Type *</label>
                <select
                  id="furnitureType"
                  name="furnitureType"
                  value={formData.furnitureType}
                  onChange={handleChange}
                  required
                >
                  <option value="">Select Furniture Type</option>
                  <option value="sofa">Sofa</option>
                  <option value="chair">Chair</option>
                  <option value="table">Table</option>
                  <option value="bed">Bed</option>
                  <option value="cabinet">Cabinet</option>
                </select>
              </div>

              <div className="form-group">
                <label htmlFor="quantity">Quantity *</label>
                <input
                  type="number"
                  id="quantity"
                  name="quantity"
                  min="1"
                  value={formData.quantity}
                  onChange={handleChange}
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="color">Color *</label>
                <input
                  type="text"
                  id="color"
                  name="color"
                  value={formData.color}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>

            <div className="form-group">
              <label htmlFor="deliveryDate">Preferred Delivery Date *</label>
              <input
                type="date"
                id="deliveryDate"
                name="deliveryDate"
                value={formData.deliveryDate}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="specialInstructions">Special Instructions</label>
              <textarea
                id="specialInstructions"
                name="specialInstructions"
                value={formData.specialInstructions}
                onChange={handleChange}
                rows={4}
              />
            </div>
          </div>

          <button type="submit" className="submit-button">Submit Order</button>
        </form>
      </div>
    </div>
  );
}

export default App;
