interface OrderForm {
  // Billing Information
  billingName: string;
  billingEmail: string;
  billingPhone: string;
  billingAddress: string;
  billingCity: string;
  billingState: string;
  billingZipCode: string;

  // Shipping Information
  shippingName: string;
  shippingEmail: string;
  shippingPhone: string;
  shippingAddress: string;
  shippingCity: string;
  shippingState: string;
  shippingZipCode: string;

  // Order Details
  furnitureType: string;
  quantity: number;
  color: string;
  deliveryDate: string;
  specialInstructions: string;
}

function App() {
  const [formData, setFormData] = useState<OrderForm>({
    // Billing Information
    billingName: '',
    billingEmail: '',
    billingPhone: '',
    billingAddress: '',
    billingCity: '',
    billingState: '',
    billingZipCode: '',

    // Shipping Information
    shippingName: '',
    shippingEmail: '',
    shippingPhone: '',
    shippingAddress: '',
    shippingCity: '',
    shippingState: '',
    shippingZipCode: '',

    // Order Details
    furnitureType: '',
    quantity: 1,
    color: '',
    deliveryDate: '',
    specialInstructions: ''
  });

  const [phoneError, setPhoneError] = useState<{ billing?: string; shipping?: string }>({});

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));

    // Phone number validation
    if (name === 'billingPhone' || name === 'shippingPhone') {
      const phoneNumber = value.replace(/\D/g, ''); // Remove non-digits
      if (phoneNumber.length !== 10) {
        setPhoneError(prev => ({
          ...prev,
          [name === 'billingPhone' ? 'billing' : 'shipping']: 'Phone number must be 10 digits'
        }));
      } else {
        setPhoneError(prev => ({
          ...prev,
          [name === 'billingPhone' ? 'billing' : 'shipping']: undefined
        }));
      }
    }
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    const billingPhone = formData.billingPhone.replace(/\D/g, '');
    const shippingPhone = formData.shippingPhone.replace(/\D/g, '');

    if (billingPhone.length !== 10 || shippingPhone.length !== 10) {
      setPhoneError({
        billing: billingPhone.length !== 10 ? 'Phone number must be 10 digits' : undefined,
        shipping: shippingPhone.length !== 10 ? 'Phone number must be 10 digits' : undefined
      });
      return;
    }

    console.log('Form submitted:', formData);
    alert('Order submitted successfully!');
  };

  return (
    <div className="App">
      <div className="container">
        <div className="header">
          <img src="/images/logo.png" alt="Carolina Furniture Concepts Logo" className="logo" />
          <h1>Carolina Furniture Concepts</h1>
          <p className="subtitle">Order Form</p>
        </div>

        <form onSubmit={handleSubmit} className="order-form">
          <div className="form-section">
            <h2>Billing Information</h2>
            <div className="form-group">
              <label htmlFor="billingName">Full Name *</label>
              <input
                type="text"
                id="billingName"
                name="billingName"
                value={formData.billingName}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="billingEmail">Email *</label>
              <input
                type="email"
                id="billingEmail"
                name="billingEmail"
                value={formData.billingEmail}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="billingPhone">Phone Number *</label>
              <div className="phone-input-container">
                <select className="country-code" disabled>
                  <option value="+1">+1</option>
                </select>
                <input
                  type="tel"
                  id="billingPhone"
                  name="billingPhone"
                  value={formData.billingPhone}
                  onChange={handleChange}
                  placeholder="(555) 555-5555"
                  pattern="[0-9]{10}"
                  required
                />
              </div>
              {phoneError.billing && <span className="error-message">{phoneError.billing}</span>}
            </div>

            <div className="form-group">
              <label htmlFor="billingAddress">Street Address *</label>
              <input
                type="text"
                id="billingAddress"
                name="billingAddress"
                value={formData.billingAddress}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-row">
              <div className="form-group">
                <label htmlFor="billingCity">City *</label>
                <input
                  type="text"
                  id="billingCity"
                  name="billingCity"
                  value={formData.billingCity}
                  onChange={handleChange}
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="billingState">State *</label>
                <input
                  type="text"
                  id="billingState"
                  name="billingState"
                  value={formData.billingState}
                  onChange={handleChange}
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="billingZipCode">ZIP Code *</label>
                <input
                  type="text"
                  id="billingZipCode"
                  name="billingZipCode"
                  value={formData.billingZipCode}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>
          </div>

          <div className="form-section">
            <h2>Shipping Information</h2>
            <div className="form-group">
              <label htmlFor="shippingName">Full Name *</label>
              <input
                type="text"
                id="shippingName"
                name="shippingName"
                value={formData.shippingName}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="shippingEmail">Email *</label>
              <input
                type="email"
                id="shippingEmail"
                name="shippingEmail"
                value={formData.shippingEmail}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="shippingPhone">Phone Number *</label>
              <div className="phone-input-container">
                <select className="country-code" disabled>
                  <option value="+1">+1</option>
                </select>
                <input
                  type="tel"
                  id="shippingPhone"
                  name="shippingPhone"
                  value={formData.shippingPhone}
                  onChange={handleChange}
                  placeholder="(555) 555-5555"
                  pattern="[0-9]{10}"
                  required
                />
              </div>
              {phoneError.shipping && <span className="error-message">{phoneError.shipping}</span>}
            </div>

            <div className="form-group">
              <label htmlFor="shippingAddress">Street Address *</label>
              <input
                type="text"
                id="shippingAddress"
                name="shippingAddress"
                value={formData.shippingAddress}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-row">
              <div className="form-group">
                <label htmlFor="shippingCity">City *</label>
                <input
                  type="text"
                  id="shippingCity"
                  name="shippingCity"
                  value={formData.shippingCity}
                  onChange={handleChange}
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="shippingState">State *</label>
                <input
                  type="text"
                  id="shippingState"
                  name="shippingState"
                  value={formData.shippingState}
                  onChange={handleChange}
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="shippingZipCode">ZIP Code *</label>
                <input
                  type="text"
                  id="shippingZipCode"
                  name="shippingZipCode"
                  value={formData.shippingZipCode}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>
          </div>

          <div className="form-section">
            <h2>Order Details</h2>
            <div className="form-row">
              <div className="form-group">
                <label htmlFor="furnitureType">Furniture Type *</label>
                <select
                  id="furnitureType"
                  name="furnitureType"
                  value={formData.furnitureType}
                  onChange={handleChange}
                  required
                >
                  <option value="">Select Furniture Type</option>
                  <option value="sofa">Sofa</option>
                  <option value="chair">Chair</option>
                  <option value="table">Table</option>
                  <option value="bed">Bed</option>
                  <option value="cabinet">Cabinet</option>
                </select>
              </div>

              <div className="form-group">
                <label htmlFor="quantity">Quantity *</label>
                <input
                  type="number"
                  id="quantity"
                  name="quantity"
                  min="1"
                  value={formData.quantity}
                  onChange={handleChange}
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="color">Color *</label>
                <input
                  type="text"
                  id="color"
                  name="color"
                  value={formData.color}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>

            <div className="form-group">
              <label htmlFor="deliveryDate">Preferred Delivery Date *</label>
              <input
                type="date"
                id="deliveryDate"
                name="deliveryDate"
                value={formData.deliveryDate}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="specialInstructions">Special Instructions</label>
              <textarea
                id="specialInstructions"
                name="specialInstructions"
                value={formData.specialInstructions}
                onChange={handleChange}
                rows={4}
              />
            </div>
          </div>

          <button type="submit" className="submit-button">Submit Order</button>
        </form>
      </div>
    </div>
  );
}

export default App;
