import React, { useState } from 'react';
import './App.css';

interface OrderForm {
  // Store Selection
  selectedStore: string;

  // Billing Information
  billingName: string;
  billingEmail: string;
  billingAreaCode: string;
  billingPrefix: string;
  billingLineNumber: string;
  billingAddress: string;
  billingCity: string;
  billingState: string;
  billingZipCode: string;

  // Shipping Information
  shippingName: string;
  shippingEmail: string;
  shippingAreaCode: string;
  shippingPrefix: string;
  shippingLineNumber: string;
  shippingAddress: string;
  shippingCity: string;
  shippingState: string;
  shippingZipCode: string;

  // Order Details
  furnitureType: string;
  quantity: number;
  color: string;
  deliveryDate: string;
  specialInstructions: string;
}

function App() {
  const [formData, setFormData] = useState<OrderForm>({
    // Store Selection
    selectedStore: '',

    // Billing Information
    billingName: '',
    billingEmail: '',
    billingAreaCode: '',
    billingPrefix: '',
    billingLineNumber: '',
    billingAddress: '',
    billingCity: '',
    billingState: '',
    billingZipCode: '',

    // Shipping Information
    shippingName: '',
    shippingEmail: '',
    shippingAreaCode: '',
    shippingPrefix: '',
    shippingLineNumber: '',
    shippingAddress: '',
    shippingCity: '',
    shippingState: '',
    shippingZipCode: '',

    // Order Details
    furnitureType: '',
    quantity: 1,
    color: '',
    deliveryDate: '',
    specialInstructions: ''
  });

  const [phoneError, setPhoneError] = useState<{ billing?: string; shipping?: string }>({});

  const storeDetails = {
    'Arden, NC': {
      address: '100 Airport Rd. Arden, NC 28704',
      phone: '828-681-5011',
      fax: '828-681-5022'
    },
    'Waynesville, NC': {
      address: '121 Eagles Nest Rd, Waynesville, NC 28786',
      phone: '828-454-9293',
      fax: '828-681-5022'
    }
  };

  const handlePhoneChange = (
    e: React.ChangeEvent<HTMLInputElement>,
    type: 'billing' | 'shipping',
    part: 'AreaCode' | 'Prefix' | 'LineNumber'
  ) => {
    const value = e.target.value.replace(/\D/g, '');
    const maxLength = part === 'AreaCode' ? 3 : part === 'Prefix' ? 3 : 4;

    if (value.length <= maxLength) {
      setFormData(prev => ({
        ...prev,
        [`${type}${part}`]: value
      }));

      // Validate complete phone number
      const areaCode = part === 'AreaCode' ? value : formData[`${type}AreaCode` as keyof OrderForm];
      const prefix = part === 'Prefix' ? value : formData[`${type}Prefix` as keyof OrderForm];
      const lineNumber = part === 'LineNumber' ? value : formData[`${type}LineNumber` as keyof OrderForm];

      const completeNumber = `${areaCode}${prefix}${lineNumber}`;
      if (completeNumber.length === 10) {
        setPhoneError(prev => ({
          ...prev,
          [type]: undefined
        }));
      } else {
        setPhoneError(prev => ({
          ...prev,
          [type]: 'Please enter a complete 10-digit phone number'
        }));
      }
    }
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    const billingNumber = `${formData.billingAreaCode}${formData.billingPrefix}${formData.billingLineNumber}`;
    const shippingNumber = `${formData.shippingAreaCode}${formData.shippingPrefix}${formData.shippingLineNumber}`;

    if (billingNumber.length !== 10 || shippingNumber.length !== 10) {
      setPhoneError({
        billing: billingNumber.length !== 10 ? 'Please enter a complete 10-digit phone number' : undefined,
        shipping: shippingNumber.length !== 10 ? 'Please enter a complete 10-digit phone number' : undefined
      });
      return;
    }

    console.log('Form submitted:', formData);
    alert('Order submitted successfully!');
  };

  const renderPhoneInput = (type: 'billing' | 'shipping') => (
    <div className="form-group">
      <label htmlFor={`${type}Phone`}>Phone Number *</label>
      <div className="phone-input-container">
        <select className="country-code" disabled>
          <option value="+1">+1</option>
        </select>
        <div className="phone-parts">
          <input
            type="text"
            id={`${type}AreaCode`}
            name={`${type}AreaCode`}
            value={formData[`${type}AreaCode` as keyof OrderForm]}
            onChange={(e) => handlePhoneChange(e, type, 'AreaCode')}
            placeholder="555"
            maxLength={3}
            required
            className="phone-part"
          />
          <span className="phone-separator">-</span>
          <input
            type="text"
            id={`${type}Prefix`}
            name={`${type}Prefix`}
            value={formData[`${type}Prefix` as keyof OrderForm]}
            onChange={(e) => handlePhoneChange(e, type, 'Prefix')}
            placeholder="555"
            maxLength={3}
            required
            className="phone-part"
          />
          <span className="phone-separator">-</span>
          <input
            type="text"
            id={`${type}LineNumber`}
            name={`${type}LineNumber`}
            value={formData[`${type}LineNumber` as keyof OrderForm]}
            onChange={(e) => handlePhoneChange(e, type, 'LineNumber')}
            placeholder="5555"
            maxLength={4}
            required
            className="phone-part"
          />
        </div>
      </div>
      {phoneError[type] && <span className="error-message">{phoneError[type]}</span>}
    </div>
  );

  return (
    <div className="App">
      <div className="container">
        <div className="header">
          <img src="/images/logo.png" alt="Carolina Furniture Concepts Logo" className="logo" />
          <h1>Carolina Furniture Concepts</h1>
          <p className="subtitle">Order Form</p>
        </div>

        <form onSubmit={handleSubmit} className="order-form">
          <div className="form-section">
            <h2>Store Selection</h2>
            <div className="form-group">
              <label htmlFor="selectedStore">Select Store *</label>
              <select
                id="selectedStore"
                name="selectedStore"
                value={formData.selectedStore}
                onChange={handleChange}
                required
              >
                <option value="">Select a Store</option>
                <option value="Arden, NC">Arden, NC</option>
                <option value="Waynesville, NC">Waynesville, NC</option>
              </select>
            </div>

            {formData.selectedStore && (
              <div className="store-details">
                <p><strong>Address:</strong> {storeDetails[formData.selectedStore as keyof typeof storeDetails].address}</p>
                <p><strong>Phone:</strong> {storeDetails[formData.selectedStore as keyof typeof storeDetails].phone}</p>
                <p><strong>Fax:</strong> {storeDetails[formData.selectedStore as keyof typeof storeDetails].fax}</p>
              </div>
            )}
          </div>

          <div className="form-section">
            <h2>Billing Information</h2>
            <div className="form-group">
              <label htmlFor="billingName">Full Name *</label>
              <input
                type="text"
                id="billingName"
                name="billingName"
                value={formData.billingName}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="billingEmail">Email *</label>
              <input
                type="email"
                id="billingEmail"
                name="billingEmail"
                value={formData.billingEmail}
                onChange={handleChange}
                required
              />
            </div>

            {renderPhoneInput('billing')}

            <div className="form-group">
              <label htmlFor="billingAddress">Street Address *</label>
              <input
                type="text"
                id="billingAddress"
                name="billingAddress"
                value={formData.billingAddress}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-row">
              <div className="form-group">
                <label htmlFor="billingCity">City *</label>
                <input
                  type="text"
                  id="billingCity"
                  name="billingCity"
                  value={formData.billingCity}
                  onChange={handleChange}
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="billingState">State *</label>
                <input
                  type="text"
                  id="billingState"
                  name="billingState"
                  value={formData.billingState}
                  onChange={handleChange}
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="billingZipCode">ZIP Code *</label>
                <input
                  type="text"
                  id="billingZipCode"
                  name="billingZipCode"
                  value={formData.billingZipCode}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>
          </div>

          <div className="form-section">
            <h2>Shipping Information</h2>
            <div className="form-group">
              <label htmlFor="shippingName">Full Name *</label>
              <input
                type="text"
                id="shippingName"
                name="shippingName"
                value={formData.shippingName}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="shippingEmail">Email *</label>
              <input
                type="email"
                id="shippingEmail"
                name="shippingEmail"
                value={formData.shippingEmail}
                onChange={handleChange}
                required
              />
            </div>

            {renderPhoneInput('shipping')}

            <div className="form-group">
              <label htmlFor="shippingAddress">Street Address *</label>
              <input
                type="text"
                id="shippingAddress"
                name="shippingAddress"
                value={formData.shippingAddress}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-row">
              <div className="form-group">
                <label htmlFor="shippingCity">City *</label>
                <input
                  type="text"
                  id="shippingCity"
                  name="shippingCity"
                  value={formData.shippingCity}
                  onChange={handleChange}
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="shippingState">State *</label>
                <input
                  type="text"
                  id="shippingState"
                  name="shippingState"
                  value={formData.shippingState}
                  onChange={handleChange}
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="shippingZipCode">ZIP Code *</label>
                <input
                  type="text"
                  id="shippingZipCode"
                  name="shippingZipCode"
                  value={formData.shippingZipCode}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>
          </div>

          <div className="form-section">
            <h2>Order Details</h2>
            <div className="form-row">
              <div className="form-group">
                <label htmlFor="furnitureType">Furniture Type *</label>
                <select
                  id="furnitureType"
                  name="furnitureType"
                  value={formData.furnitureType}
                  onChange={handleChange}
                  required
                >
                  <option value="">Select Furniture Type</option>
                  <option value="sofa">Sofa</option>
                  <option value="chair">Chair</option>
                  <option value="table">Table</option>
                  <option value="bed">Bed</option>
                  <option value="cabinet">Cabinet</option>
                </select>
              </div>

              <div className="form-group">
                <label htmlFor="quantity">Quantity *</label>
                <input
                  type="number"
                  id="quantity"
                  name="quantity"
                  min="1"
                  value={formData.quantity}
                  onChange={handleChange}
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="color">Color *</label>
                <input
                  type="text"
                  id="color"
                  name="color"
                  value={formData.color}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>

            <div className="form-group">
              <label htmlFor="deliveryDate">Preferred Delivery Date *</label>
              <input
                type="date"
                id="deliveryDate"
                name="deliveryDate"
                value={formData.deliveryDate}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="specialInstructions">Special Instructions</label>
              <textarea
                id="specialInstructions"
                name="specialInstructions"
                value={formData.specialInstructions}
                onChange={handleChange}
                rows={4}
              />
            </div>
          </div>

          <button type="submit" className="submit-button">Submit Order</button>
        </form>
      </div>
    </div>
  );
}

export default App;
