import React, { useState } from 'react';
import './App.css';

interface PhoneNumber {
  type: 'cell' | 'home' | '';
  areaCode: string;
  prefix: string;
  lineNumber: string;
}

interface OrderForm {
  // Store Selection
  selectedStore: string;

  // Billing Information
  billingName: string;
  billingEmail: string;
  billingPhones: PhoneNumber[];
  billingAddress: string;
  billingCity: string;
  billingState: string;
  billingZipCode: string;

  // Shipping Information
  shippingName: string;
  shippingEmail: string;
  shippingPhones: PhoneNumber[];
  shippingAddress: string;
  shippingCity: string;
  shippingState: string;
  shippingZipCode: string;

  // Order Details
  furnitureType: string;
  quantity: number;
  color: string;
  deliveryDate: string;
  specialInstructions: string;
}

function App() {
  const [formData, setFormData] = useState<OrderForm>({
    // Store Selection
    selectedStore: '',

    // Billing Information
    billingName: '',
    billingEmail: '',
    billingPhones: [{ type: '', areaCode: '', prefix: '', lineNumber: '' }],
    billingAddress: '',
    billingCity: '',
    billingState: '',
    billingZipCode: '',

    // Shipping Information
    shippingName: '',
    shippingEmail: '',
    shippingPhones: [{ type: '', areaCode: '', prefix: '', lineNumber: '' }],
    shippingAddress: '',
    shippingCity: '',
    shippingState: '',
    shippingZipCode: '',

    // Order Details
    furnitureType: '',
    quantity: 1,
    color: '',
    deliveryDate: '',
    specialInstructions: ''
  });

  const [phoneError, setPhoneError] = useState<{ billing?: string[]; shipping?: string[] }>({});
  const [phoneTypeError, setPhoneTypeError] = useState<{ billing?: string[]; shipping?: string[] }>({});

  const storeDetails = {
    'Arden, NC': {
      address: '100 Airport Rd. Arden, NC 28704',
      phone: '828-681-5011',
      fax: '828-681-5022'
    },
    'Waynesville, NC': {
      address: '121 Eagles Nest Rd, Waynesville, NC 28786',
      phone: '828-454-9293',
      fax: '828-681-5022'
    }
  };

  const handlePhoneChange = (
    e: React.ChangeEvent<HTMLInputElement>,
    type: 'billing' | 'shipping',
    index: number,
    part: 'areaCode' | 'prefix' | 'lineNumber'
  ) => {
    const value = e.target.value.replace(/\D/g, '');
    const maxLength = part === 'areaCode' ? 3 : part === 'prefix' ? 3 : 4;

    if (value.length <= maxLength) {
      setFormData(prev => ({
        ...prev,
        [`${type}Phones`]: prev[`${type}Phones` as keyof OrderForm].map((phone, i) => 
          i === index ? { ...phone, [part]: value } : phone
        )
      }));

      // Validate complete phone number
      const phones = formData[`${type}Phones` as keyof OrderForm] as PhoneNumber[];
      const currentPhone = phones[index];
      const completeNumber = `${currentPhone.areaCode}${currentPhone.prefix}${currentPhone.lineNumber}`;

      if (completeNumber.length === 10) {
        setPhoneError(prev => ({
          ...prev,
          [type]: prev[type]?.map((error, i) => i === index ? undefined : error)
        }));
      } else {
        setPhoneError(prev => ({
          ...prev,
          [type]: prev[type]?.map((error, i) => i === index ? 'Please enter a complete 10-digit phone number' : error)
        }));
      }
    }
  };

  const addPhoneNumber = (type: 'billing' | 'shipping') => {
    setFormData(prev => ({
      ...prev,
      [`${type}Phones`]: [...prev[`${type}Phones` as keyof OrderForm], { type: '', areaCode: '', prefix: '', lineNumber: '' }]
    }));
  };

  const removePhoneNumber = (type: 'billing' | 'shipping', index: number) => {
    setFormData(prev => ({
      ...prev,
      [`${type}Phones`]: prev[`${type}Phones` as keyof OrderForm].filter((_, i) => i !== index)
    }));
  };

  const handlePhoneTypeChange = (type: 'billing' | 'shipping', index: number, value: string) => {
    setFormData(prev => ({
      ...prev,
      [`${type}Phones`]: prev[`${type}Phones` as keyof OrderForm].map((phone, i) => 
        i === index ? { ...phone, type: value as 'cell' | 'home' | '' } : phone
      )
    }));
    setPhoneTypeError(prev => ({
      ...prev,
      [type]: prev[type]?.map((error, i) => i === index ? undefined : error)
    }));
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    // Check if at least one phone number is provided for each section
    const billingHasPhone = formData.billingPhones.some(phone => phone.type);
    const shippingHasPhone = formData.shippingPhones.some(phone => phone.type);

    if (!billingHasPhone || !shippingHasPhone) {
      setPhoneTypeError({
        billing: billingHasPhone ? undefined : ['Please provide at least one phone number (Cell or Home)'],
        shipping: shippingHasPhone ? undefined : ['Please provide at least one phone number (Cell or Home)']
      });
      return;
    }

    // Validate phone numbers if type is selected
    const billingErrors = formData.billingPhones.map((phone, index) => {
      if (phone.type && `${phone.areaCode}${phone.prefix}${phone.lineNumber}`.length !== 10) {
        return 'Please enter a complete 10-digit phone number';
      }
      return undefined;
    });

    const shippingErrors = formData.shippingPhones.map((phone, index) => {
      if (phone.type && `${phone.areaCode}${phone.prefix}${phone.lineNumber}`.length !== 10) {
        return 'Please enter a complete 10-digit phone number';
      }
      return undefined;
    });

    if (billingErrors.some(error => error) || shippingErrors.some(error => error)) {
      setPhoneError({
        billing: billingErrors,
        shipping: shippingErrors
      });
      return;
    }

    console.log('Form submitted:', formData);
    alert('Order submitted successfully!');
  };

  const renderPhoneInputs = (type: 'billing' | 'shipping') => (
    <div className="form-group">
      <label htmlFor={`${type}Phone`}>Phone Numbers</label>
      {formData[`${type}Phones` as keyof OrderForm].map((phone, index) => (
        <div key={index} className="phone-input-container">
          <select 
            className="phone-type"
            value={phone.type}
            onChange={(e) => handlePhoneTypeChange(type, index, e.target.value)}
          >
            <option value="">Select Type</option>
            <option value="cell">Cell</option>
            <option value="home">Home</option>
          </select>
          <select className="country-code" disabled>
            <option value="+1">+1</option>
          </select>
          <div className="phone-parts">
            <input
              type="text"
              id={`${type}AreaCode${index}`}
              name={`${type}AreaCode${index}`}
              value={phone.areaCode}
              onChange={(e) => handlePhoneChange(e, type, index, 'areaCode')}
              placeholder="555"
              maxLength={3}
              className="phone-part"
              disabled={!phone.type}
            />
            <span className="phone-separator">-</span>
            <input
              type="text"
              id={`${type}Prefix${index}`}
              name={`${type}Prefix${index}`}
              value={phone.prefix}
              onChange={(e) => handlePhoneChange(e, type, index, 'prefix')}
              placeholder="555"
              maxLength={3}
              className="phone-part"
              disabled={!phone.type}
            />
            <span className="phone-separator">-</span>
            <input
              type="text"
              id={`${type}LineNumber${index}`}
              name={`${type}LineNumber${index}`}
              value={phone.lineNumber}
              onChange={(e) => handlePhoneChange(e, type, index, 'lineNumber')}
              placeholder="5555"
              maxLength={4}
              className="phone-part"
              disabled={!phone.type}
            />
          </div>
          {index > 0 && (
            <button
              type="button"
              className="remove-phone"
              onClick={() => removePhoneNumber(type, index)}
            >
              Remove
            </button>
          )}
        </div>
      ))}
      <button
        type="button"
        className="add-phone"
        onClick={() => addPhoneNumber(type)}
      >
        Add Another Phone Number
      </button>
      {phoneError[type]?.map((error, index) => 
        error && <span key={index} className="error-message">{error}</span>
      )}
      {phoneTypeError[type]?.map((error, index) => 
        error && <span key={index} className="error-message">{error}</span>
      )}
    </div>
  );

  return (
    <div className="App">
      <div className="container">
        <div className="header">
          <img src="/images/logo.png" alt="Carolina Furniture Concepts Logo" className="logo" />
          <h1>Carolina Furniture Concepts</h1>
          <p className="subtitle">Order Form</p>
        </div>

        <form onSubmit={handleSubmit} className="order-form">
          <div className="form-section">
            <h2>Store Selection</h2>
            <div className="form-group">
              <label htmlFor="selectedStore">Select Store *</label>
              <select
                id="selectedStore"
                name="selectedStore"
                value={formData.selectedStore}
                onChange={handleChange}
                required
              >
                <option value="">Select a Store</option>
                <option value="Arden, NC">Arden, NC</option>
                <option value="Waynesville, NC">Waynesville, NC</option>
              </select>
            </div>

            {formData.selectedStore && (
              <div className="store-details">
                <p><strong>Address:</strong> {storeDetails[formData.selectedStore as keyof typeof storeDetails].address}</p>
                <p><strong>Phone:</strong> {storeDetails[formData.selectedStore as keyof typeof storeDetails].phone}</p>
                <p><strong>Fax:</strong> {storeDetails[formData.selectedStore as keyof typeof storeDetails].fax}</p>
              </div>
            )}
          </div>

          <div className="form-section">
            <h2>Billing Information</h2>
            <div className="form-group">
              <label htmlFor="billingName">Full Name *</label>
              <input
                type="text"
                id="billingName"
                name="billingName"
                value={formData.billingName}
                onChange={handleChange}
              />
            </div>

            <div className="form-group">
              <label htmlFor="billingEmail">Email *</label>
              <input
                type="email"
                id="billingEmail"
                name="billingEmail"
                value={formData.billingEmail}
                onChange={handleChange}
                required
              />
            </div>

            {renderPhoneInputs('billing')}

            <div className="form-group">
              <label htmlFor="billingAddress">Street Address *</label>
              <input
                type="text"
                id="billingAddress"
                name="billingAddress"
                value={formData.billingAddress}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-row">
              <div className="form-group">
                <label htmlFor="billingCity">City *</label>
                <input
                  type="text"
                  id="billingCity"
                  name="billingCity"
                  value={formData.billingCity}
                  onChange={handleChange}
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="billingState">State *</label>
                <input
                  type="text"
                  id="billingState"
                  name="billingState"
                  value={formData.billingState}
                  onChange={handleChange}
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="billingZipCode">ZIP Code *</label>
                <input
                  type="text"
                  id="billingZipCode"
                  name="billingZipCode"
                  value={formData.billingZipCode}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>
          </div>

          <div className="form-section">
            <h2>Shipping Information</h2>
            <div className="form-group">
              <label htmlFor="shippingName">Full Name *</label>
              <input
                type="text"
                id="shippingName"
                name="shippingName"
                value={formData.shippingName}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="shippingEmail">Email *</label>
              <input
                type="email"
                id="shippingEmail"
                name="shippingEmail"
                value={formData.shippingEmail}
                onChange={handleChange}
                required
              />
            </div>

            {renderPhoneInputs('shipping')}

            <div className="form-group">
              <label htmlFor="shippingAddress">Street Address *</label>
              <input
                type="text"
                id="shippingAddress"
                name="shippingAddress"
                value={formData.shippingAddress}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-row">
              <div className="form-group">
                <label htmlFor="shippingCity">City *</label>
                <input
                  type="text"
                  id="shippingCity"
                  name="shippingCity"
                  value={formData.shippingCity}
                  onChange={handleChange}
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="shippingState">State *</label>
                <input
                  type="text"
                  id="shippingState"
                  name="shippingState"
                  value={formData.shippingState}
                  onChange={handleChange}
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="shippingZipCode">ZIP Code *</label>
                <input
                  type="text"
                  id="shippingZipCode"
                  name="shippingZipCode"
                  value={formData.shippingZipCode}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>
          </div>

          <div className="form-section">
            <h2>Order Details</h2>
            <div className="form-row">
              <div className="form-group">
                <label htmlFor="furnitureType">Furniture Type *</label>
                <select
                  id="furnitureType"
                  name="furnitureType"
                  value={formData.furnitureType}
                  onChange={handleChange}
                  required
                >
                  <option value="">Select Furniture Type</option>
                  <option value="sofa">Sofa</option>
                  <option value="chair">Chair</option>
                  <option value="table">Table</option>
                  <option value="bed">Bed</option>
                  <option value="cabinet">Cabinet</option>
                </select>
              </div>

              <div className="form-group">
                <label htmlFor="quantity">Quantity *</label>
                <input
                  type="number"
                  id="quantity"
                  name="quantity"
                  min="1"
                  value={formData.quantity}
                  onChange={handleChange}
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="color">Color *</label>
                <input
                  type="text"
                  id="color"
                  name="color"
                  value={formData.color}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>

            <div className="form-group">
              <label htmlFor="deliveryDate">Preferred Delivery Date *</label>
              <input
                type="date"
                id="deliveryDate"
                name="deliveryDate"
                value={formData.deliveryDate}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="specialInstructions">Special Instructions</label>
              <textarea
                id="specialInstructions"
                name="specialInstructions"
                value={formData.specialInstructions}
                onChange={handleChange}
                rows={4}
              />
            </div>
          </div>

          <button type="submit" className="submit-button">Submit Order</button>
        </form>
      </div>
    </div>
  );
}

export default App;
